function debounce(fn,wait){let timeoutId=null;return function(...args){if(timeoutId)clearTimeout(timeoutId);const context=this;timeoutId=setTimeout(()=>fn.apply(context,args),wait)}}function Logger(prefix){function log(message,...rest){console.log(prefix+" "+message,...rest)}log.error=function(message,...rest){console.error(prefix+" "+message,...rest)};log.warn=function(message,...rest){console.warn(prefix+" "+message,...rest)};log.debug=function(message,...rest){console.debug(prefix+" "+message,...rest)};return log}if(typeof module!=="undefined"&&module.exports){module.exports={debounce:debounce,Logger:Logger}}if(typeof window!=="undefined"){window.debounce=debounce;window.Logger=Logger}
